def first_basement_position(elevator_instructions)
	current_floor = 0
	elevator_instructions.split("").each_with_index do |i, index|
		if current_floor == -1
			return index
		else

			if i == "("
				current_floor += 1
			else
				current_floor -= 1 
			end
		end
	end  
end

